	getApiItemTransformationConfigurationWithDefaults,
	getDefaultValueBlock,
	getDeprecatedBlock,
	getExampleBlocks,
	getFilePathForApiItem,
	getHeadingForApiItem,
	getLinkForApiItem,
	getModifiers,
	getQualifiedApiItemName,
	getReturnsBlock,
